import React, { useState } from "react";

import styles from "./costumizacao.module.css"

import { FaCaretLeft, FaCaretRight } from "react-icons/fa6";

import planoDeFundo1 from "../../Assets/Personagem/PlanoDeFundo/Tela Batalha Chuvosa.jpg"
import planoDeFundo2 from "../../Assets/Personagem/PlanoDeFundo/Tela Batalha Nublado.jpg"
import planoDeFundo3 from "../../Assets/Personagem/PlanoDeFundo/Tela Tabalha Neve.jpg"
import planoDeFundo4 from "../../Assets/Personagem/PlanoDeFundo/Tela Batalha.jpg"

import Menu from "../../Components/Menu/Menu.js"



export default function Trelo() {
        var baseHp = 100;
		var attackDamage = 10;
		var baseStamina = 10;
		var xpAmountToLevelUp = 100;
		var level = 1;
		var xp = 0;
		var stamina = 10;
		var hp = 100;
		function allowDrop(ev) {
			ev.preventDefault();
		}

		function drag(ev) {
			ev.dataTransfer.setData("text", ev.target.id);
		}

		function drop(ev) {
			ev.preventDefault();
			var data = ev.dataTransfer.getData("text");
			var element = document.getElementById(data);
			var parent = element.parentElement.id;
			console.log(parent + " " + ev.target.id);
			var target = ev.target;
			if(target.id.includes("drag"))
				target = target.parentElement;
			if(target.id === parent || target.id === element.id)
				return;

			target.appendChild(element);

			if(parent === "lista1")
			{
				xp += 5;
			}
			else if(parent === "lista4")
			{
				xp -= 20;
			}

			if(target.id === "lista1")
			{
				xp -= 5;
			}
			else if(target.id === "lista4")
			{
				xp += 20;
			}

			if(xp < 0 && level > 1)
			{
				LevelDown();
				return;
			}


			if(xp >= xpAmountToLevelUp)
			{
				LevelUp();
			}
			else
				UpdateXP();

		}

		function LevelUp()
		{
			baseHp += 10;
			attackDamage += 2;
			xp -= xpAmountToLevelUp;
			xpAmountToLevelUp += 30;
			hp = baseHp;
			if(!(level % 2))
				baseStamina++; 
			stamina = baseStamina;
			level++;
			UpdateStats();
		}

		function LevelDown()
		{
			baseHp -= 10;
			attackDamage -= 2;
			xpAmountToLevelUp -= 30;
			xp = xpAmountToLevelUp + xp;
			hp = baseHp;
			if((level % 2))
				baseStamina--; 
			stamina = baseStamina;
			level--;
			UpdateStats();
		}

		function UpdateXP()
		{
			document.getElementById("xp").innerHTML = "Experience: " + xp;
			document.getElementById("xpAmountToLevelUp").innerHTML = xpAmountToLevelUp;
		}
		function UpdateStats()
		{
			// Update Level, Hp, Stamina and Attack Damage text
			document.getElementById("xp").innerHTML = "Experience: " + xp;
			document.getElementById("xpAmountToLevelUp").innerHTML = xpAmountToLevelUp;
			document.getElementById("level").innerHTML = "Level: " + level;
			document.getElementById("hp").innerHTML = "HP: " + hp;
			document.getElementById("baseHp").innerHTML = baseHp;
			document.getElementById("stamina").innerHTML = "Stamina: " + stamina;
			document.getElementById("baseStamina").innerHTML = baseStamina;
			document.getElementById("attackDamage").innerHTML = "Attack Damage: " + attackDamage;
		}

